// æœè£…åˆ†ç±»
export type ClothingCategory = 'all' | 'tops' | 'bottoms' | 'dresses';

// æœè£…æ•°æ®
export interface ClothingItem {
  id: string;
  name: string;
  category: Exclude<ClothingCategory, 'all'>;
  imageUrl: string;
  thumbnailUrl: string;
}

// åº”ç”¨æ­¥éª¤
export type AppStep = 'upload' | 'select' | 'processing' | 'result';

// å¤„ç†é˜¶æ®µ
export type ProcessingStage =
  | 'detecting'    // æ£€æµ‹äººç‰©
  | 'analyzing'    // åˆ†ææœè£…
  | 'merging'      // èåˆå¤„ç†
  | 'refining';    // ä¼˜åŒ–ç»†èŠ‚

// å¤„ç†çŠ¶æ€
export interface ProcessingStatus {
  stage: ProcessingStage;
  progress: number;  // 0-100
  message: string;
}

// æ¢è¡£ä»»åŠ¡
export interface TryOnTask {
  id: string;
  status: 'pending' | 'processing' | 'completed' | 'failed';
  progress: number;
  stage: ProcessingStage;
  createdAt: Date;
}

// æ¢è¡£ç»“æœ
export interface TryOnResult {
  taskId: string;
  originalImageUrl: string;
  resultImageUrl: string;
  clothingItem: ClothingItem;
  createdAt: Date;
}

// äººç‰©å›¾ç‰‡çŠ¶æ€
export interface PersonImageState {
  file: File | null;
  previewUrl: string | null;
  status: 'idle' | 'uploading' | 'uploaded' | 'error';
  error?: string;
}

// APIé…ç½®
export interface TryOnAPIConfig {
  endpoint: string;
  apiKey?: string;
  headers?: Record<string, string>;
}

// APIè¯·æ±‚
