        <div className="absolute bottom-0 left-0 right-0 p-4 bg-gradient-to-t from-background/80 to-transparent">
          <p className="text-sm text-foreground font-medium">äººç‰©ç…§ç‰‡å·²ä¸Šä¼ </p>
          <p className="text-xs text-muted-foreground">ç‚¹å‡»å³ä¸Šè§’å¯æ›´æ¢å›¾ç‰‡</p>
        </div>
      </div>
    );
  }

  return (
    <label
      className={cn(
        'dropzone w-full aspect-[3/4] cursor-pointer',
