import type {
  TryOnAPIConfig,
  TryOnRequest,
  TryOnResponse,
  ProcessingStage
} from '@/types';

// é»˜è®¤APIé…ç½® - å¯æ ¹æ®å®é™…APIä¿®æ”¹
const defaultConfig: TryOnAPIConfig = {
  endpoint: '/api/try-on',
  apiKey: undefined,
};

// å¤„ç†é˜¶æ®µé…ç½®
const PROCESSING_STAGES: { stage: ProcessingStage; message: string; duration: number }[] = [
  { stage: 'detecting', message: 'æ­£åœ¨æ£€æµ‹äººç‰©å§¿æ€...', duration: 2000 },
  { stage: 'analyzing', message: 'æ­£åœ¨åˆ†ææœè£…ç‰¹å¾...', duration: 2000 },
  { stage: 'merging', message: 'æ­£åœ¨æ™ºèƒ½èåˆå¤„ç†...', duration: 3000 },
  { stage: 'refining', message: 'æ­£åœ¨ä¼˜åŒ–ç»†èŠ‚æ•ˆæœ...', duration: 2000 },
];

class TryOnService {
  private config: TryOnAPIConfig;

  constructor(config: TryOnAPIConfig = defaultConfig) {
    this.config = config;
  }

  // é…ç½®API
  configure(config: Partial<TryOnAPIConfig>) {
    this.config = { ...this.config, ...config };
  }

  // æäº¤æ¢è¡£è¯·æ±‚
  async submitTryOn(request: TryOnRequest): Promise<TryOnResponse> {
    try {
      const headers: Record<string, string> = {
        'Content-Type': 'application/json',
        ...this.config.headers,
      };

      if (this.config.apiKey) {
        headers['Authorization'] = `Bearer ${this.config.apiKey}`;
      }

      const response = await fetch(this.config.endpoint, {
        method: 'POST',
        headers,
        body: JSON.stringify(request),
      });

      if (!response.ok) {
        throw new Error(`HTTP error! status: ${response.status}`);
      }

      return await response.json();
    } catch (error) {
      console.error('TryOn API error:', error);
      return {
        success: false,
        error: {
          code: 'API_ERROR',
          message: error instanceof Error ? error.message : 'è¯·æ±‚å¤±è´¥',
        },
      };
    }
  }

  // æ¨¡æ‹Ÿå¤„ç†è¿›åº¦ï¼ˆç”¨äºæ¼”ç¤ºï¼Œå®é™…æ¥å…¥APIåå¯ç§»é™¤ï¼‰
  async simulateProcessing(
    onProgress: (progress: number, stage: ProcessingStage, message: string) => void
  ): Promise<string> {
    let totalProgress = 0;
    const totalDuration = PROCESSING_STAGES.reduce((acc, s) => acc + s.duration, 0);

    for (const { stage, message, duration } of PROCESSING_STAGES) {
      const startProgress = totalProgress;
      const progressIncrement = (duration / totalDuration) * 100;
      const steps = 10;
      const stepDuration = duration / steps;

      for (let i = 0; i <= steps; i++) {
        await new Promise(resolve => setTimeout(resolve, stepDuration));
        const progress = Math.min(
          startProgress + (progressIncrement * i / steps),
          99
        );
        onProgress(Math.round(progress), stage, message);
      }

      totalProgress += progressIncrement;
    }

    onProgress(100, 'refining', 'å¤„ç†å®Œæˆï¼');

    // è¿”å›æ¨¡æ‹Ÿçš„ç»“æœå›¾ç‰‡URLï¼ˆä½¿ç”¨åŸå›¾ä½œä¸ºæ¼”ç¤ºï¼‰
    return 'simulated_result';
  }

  // å›¾ç‰‡è½¬Base64
  async fileToBase64(file: File): Promise<string> {
    return new Promise((resolve, reject) => {
      const reader = new FileReader();
      reader.readAsDataURL(file);
      reader.onload = () => resolve(reader.result as string);
      reader.onerror = (error) => reject(error);
    });
  }

  // ä¸‹è½½ç»“æœå›¾ç‰‡
  async downloadResult(imageUrl: string, filename: string = 'try-on-result.jpg') {
    try {
      const response = await fetch(imageUrl);
      const blob = await response.blob();
      const url = window.URL.createObjectURL(blob);

      const link = document.createElement('a');
      link.href = url;
      link.download = filename;
      document.body.appendChild(link);
      link.click();
      document.body.removeChild(link);

      window.URL.revokeObjectURL(url);
    } catch (error) {
      console.error('Download error:', error);
      // é™çº§æ–¹æ¡ˆï¼šç›´æ¥æ‰“å¼€å›¾ç‰‡
      window.open(imageUrl, '_blank');
    }
  }
}

// å¯¼å‡ºå•ä¾‹
export const tryOnService = new TryOnService();

// å¯¼å‡ºç±»ä»¥ä¾¿è‡ªå®šä¹‰é…ç½®
export { TryOnService };

